import axios from 'axios';

const LoginForm = ({ onLogin, onCreate, onLogout, user }) => {
  // Handler function for form submission
  const handleSubmit = async (action, e) => {
    e.preventDefault(); // Prevent the form from submitting

    const userData = {
      username: e.target.username.value,
      password: e.target.password.value,
    };

    try {
      let response;
      if (action === 'login') {
        response = await axios.post('http://localhost:<correct-port>/login', userData);
        console.log('Login Response:', response);
        alert(response.data.message); // Show the login success alert
        onLogin(response.data.user);
      } else if (action === 'create') {
        response = await axios.post('http://localhost:<correct-port>/create', userData);
        console.log('Create Response:', response);
        alert(response.data.message); // Show the user creation success alert
        onCreate(response.data.user);
      }
    } catch (error) {
      console.error('API Error:', error);
      alert(error.response?.data?.error || 'An error occurred');
    }
  };



  return (
    <div>
      {user ? (
        <div className="username-header">
          <p>
            <strong>{user.username}</strong> is logged in
          </p>
          <button onClick={onLogout}>logout</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <div>
            <input placeholder="username" name="username" required />
          </div>
          <div>
            <input placeholder="password" name="password" type="password" required />
          </div>
          <div>
          <button type="button" value="login" onClick={(e) => handleSubmit('login', e)}>Login</button>
<button type="button" value="create" onClick={(e) => handleSubmit('create', e)}>Create</button>


          </div>
        </form>
      )}
    </div>
  );
};

export default LoginForm;
