function ProductCard({ product }) {
  const navigate = useNavigate();

  const handleAddToCartClick = () => {
    navigate('/cart', { state: { item: { name: product.name, price: product.price } } });
  };

  // Extract the filename from the full path
  const imageName = path.basename(product.image);

  return (
    <div className="product-card">
      <img src={`http://localhost:5001/images/${imageName}`} alt={product.name} className="product-image" />
      <h3>{product.name}</h3>
      <p>${product.price}</p>
      <button className="add-to-cart-button" onClick={handleAddToCartClick}>Add to Cart</button>
    </div>
  );
}
