import React, { useEffect, useState } from "react";
import '../styles/Admin.css';

const AdminPage = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch all items from the backend
    fetch("http://localhost:5001/items")
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching items:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Admin Page</h1>
      <table border="1">
        <thead>
          <tr>
            <th>Order</th>
            <th>Username</th>
            <th>Items</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item._id} >
              <td>{item.order}</td>
              <td>{Object.keys(item).find((key) => key !== "_id" && key !== "order" && key !== "status")}</td>
              <td>
                {item[Object.keys(item).find((key) => key !== "_id" && key !== "order" && key !== "status")]
                  ? item[Object.keys(item).find((key) => key !== "_id" && key !== "order" && key !== "status")].map(
                      (subItem, index) => (
                        <div key={index}>
                          <strong>Item:</strong> {subItem.item} | <strong>Quantity:</strong>{" "}
                          {subItem.quantity} | <strong>Price:</strong> ${subItem.price}
                        </div>
                      )
                    )
                  : "No items"}
              </td>
              <td><span className="actuv">{item.status || "No status provided"}</span></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPage;
