import React, { useEffect, useState } from 'react';
import axios from 'axios';

const LoginForm = ({ onLogin, onCreate, onLogout }) => {
  const [user, setUser] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false); // Track submission state
  const [error, setError] = useState(''); // Track error message

  // Check if the user is already logged in when the component mounts
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get('http://localhost:5001/check-user'); // Assume this endpoint checks for logged-in user
        if (response.data.user) {
          setUser(response.data.user); // Set the user if logged in
        }
      } catch (error) {
        console.error('Error checking user:', error);
      }
    };

    fetchUser();
  }, []);

  // Handler function for form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Clear error state at the start of submission
    setError('');
    setIsSubmitting(true); // Set submitting state to true to disable form buttons

    // Grab form values
    const userData = {
      username: e.target.username.value,
      password: e.target.password.value,
    };

    // Determine which button was clicked
    const action = e.nativeEvent.submitter.value; // 'login' or 'create'

    try {
      let response;
      if (action === 'login') {
        // API call for login
        response = await axios.post('http://localhost:5001/login', userData);
        alert(response.data.message);
        setUser(response.data.user); // Set the logged-in user state
        onLogin(response.data.user); // Pass logged-in user details to parent
      } else if (action === 'create') {
        // API call for user creation
        response = await axios.post('http://localhost:5001/create', userData);
        alert(response.data.message);
        onCreate(response.data.user); // Optionally handle created user details
      }
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred'); // Set error message
    } finally {
      setIsSubmitting(false); // Reset the submitting state
    }
  };

  return (
    <div>
      {user ? (
        <div className="username-header">
          <p>
            <strong>{user.username}</strong> is logged in
          </p>
          <button onClick={() => {
            setUser(null); // Clear the user state
            onLogout(); // Call parent logout function
          }}>Logout</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <div>
            <input placeholder="Username" name="username" required />
          </div>
          <div>
            <input placeholder="Password" name="password" type="password" required />
          </div>
          <div>
            <button type="submit" value="login" disabled={isSubmitting}>Login</button>
            <button type="submit" value="create" disabled={isSubmitting}>Create Account</button>
          </div>
        </form>
      )}
      {error && <p className="error">{error}</p>} {/* Display error message if exists */}
    </div>
  );
};

export default LoginForm;
