import { useState } from "react";

const SecurityQuestionFetcher = () => {
  const [username, setUsername] = useState("");
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchQuestionAnswer = async () => {
    setLoading(true);
    setError("");
    setData(null);

    try {
      const response = await fetch(`/get-question-answer/${username}`);
      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || "Failed to fetch data");
      }

      setData(result);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h2>Get Security Question & Answer</h2>

      <input
        type="text"
        placeholder="Enter username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />

      <button onClick={fetchQuestionAnswer} disabled={!username || loading}>
        {loading ? "Fetching..." : "Get Question & Answer"}
      </button>

      {error && <p className="error">{error}</p>}

      {data && (
        <div className="result">
          <p><strong>Question:</strong> {data.question}</p>
          <p><strong>Answer:</strong> {data.answer}</p>
        </div>
      )}
    </div>
  );
};

export default SecurityQuestionFetcher;
