import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/Checkout.css';
import axios from 'axios';

function Checkout() {
  const location = useLocation();
  const navigate = useNavigate();
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [transactionId, setTransactionId] = useState('');
  const [showPopup, setShowPopup] = useState(false);

  const storedCart = localStorage.getItem('checkoutCart');
  const cartItems = location.state || (storedCart ? JSON.parse(storedCart) : []);

  const totalPrice = cartItems.reduce((total, item) => {
    const price = parseFloat(item.price);
    return !isNaN(price) ? total + price : total;
  }, 0);

  const discountCode = localStorage.getItem('discountCode');
  const discountedPrice = parseFloat(localStorage.getItem('discountedPrice')) || totalPrice;
  const isLoggedIn = localStorage.getItem('username');

  const handlePlaceOrder = () => {
    if (!isLoggedIn) {
      navigate('/'); // Redirect to login page
      return;
    }
    setShowPopup(true); // Show the popup for transaction ID
  };

  const handlePayment = () => {
    if (!transactionId) {
      alert('Please enter a transaction ID.');
      return;
    }

    const paymentData = {
      cartItems,
      discountCode,
      discountedPrice,
      usernamep: isLoggedIn,
      address,
      phone,
      transactionId, // Include transaction ID
    };

    axios.post('http://localhost:5001/save-cart', paymentData)
      .then(response => {
        console.log(response.data);
        localStorage.removeItem('cart');
        localStorage.removeItem('checkoutCart');
        localStorage.removeItem('discountCode');
        localStorage.removeItem('discountedPrice');

        navigate('/home');
      })
      .catch(error => {
        console.error('Error saving cart:', error);
      });

    setShowPopup(false); // Close popup after submission
  };

  return (
    <div className="container">
      <h1>Checkout</h1>
      <div className="cart">
        <div className="carttable">
          <div className="cart-table-header">
            <span>Item</span>
            <span>Price</span>
          </div>
          <div className="cart-table-body">
            {cartItems.length > 0 ? (
              cartItems.map((item, index) => (
                <div key={index} className="cart-table-row">
                  <span>{item.name}</span>
                  <span>${item.price}</span>
                </div>
              ))
            ) : (
              <p>Your cart is empty!</p>
            )}
          </div>
        </div>

        <div className="discount-code1">
          <input
            type="text"
            placeholder="Enter Address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
          <input
            type="text"
            placeholder="Enter Phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>

        {cartItems.length > 0 && (
          <div className="total">
            <h4>Total: ${totalPrice.toFixed(2)}</h4>
            {discountCode && (
              <>
                <h5>Discount Code Applied: {discountCode}</h5>
                <h4>Discounted Total: ${discountedPrice.toFixed(2)}</h4>
              </>
            )}
          </div>
        )}

        <button className="pay-button" onClick={handlePlaceOrder}>
          Place order
        </button>
      </div>

      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Enter Transaction ID</h3>
            <input
              type="text"
              placeholder="Transaction ID"
              value={transactionId}
              onChange={(e) => setTransactionId(e.target.value)}
            />
            <button className="confirm-button" onClick={handlePayment}>
              Pay
            </button>
            <button className="cancel-button" onClick={() => setShowPopup(false)}>
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Checkout;
