// src/components/Cart.js
import React, { useState, useEffect  } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/Cart.css'; // Optional: For styling, create a matching CSS file.

function Cart(){
  const [cartItems, setCartItems] = useState([]);
  const location = useLocation();


  useEffect(() => {
    // Add the item from the state (if available) to the cartItems
    const newItem = location.state?.item;
    if (newItem) {
      setCartItems((prevItems) => {
        // Check if the item already exists in the cart
        const isItemAlreadyInCart = prevItems.some(
          (item) => item.name === newItem.name && item.price === newItem.price
        );
        if (!isItemAlreadyInCart) {
          return [...prevItems, newItem];
        }
        return prevItems; // Return existing items if it's a duplicate
      });
    }
  }, [location.state]);

//   const addItemToCart = (item) => {
//     setCartItems((prevItems) => [...prevItems, sendData]);
//   };

  const removeItemFromCart = (index) => {
    setCartItems((prevItems) => prevItems.filter((_, i) => i !== index));
  };

  return (
    <div className="cart">
      <h2>Shopping Cart</h2>
      <ul>
        {cartItems.length > 0 ? (
          cartItems.map((item, index) => (
            <li key={index}>
              {item.name} - {item.price}
              <button onClick={() => removeItemFromCart(index)}>Remove</button>
            </li>
          ))
        ) : (
          <p>Your cart is empty!</p>
        )}
      </ul>
      {/* <button onClick={() => addItemToCart({ name: 'Sample Item', price: 10 })}>
        Add Sample Item
      </button> */}
    </div>
  );
};

export default Cart;
