import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/Checkout.css';
import { Link } from 'react-router-dom';

function Cart() {
  const location = useLocation();
  const storedCart = localStorage.getItem('checkoutCart');
  const cartItems = location.state || (storedCart ? JSON.parse(storedCart) : []);
  const mongoose = require('mongoose')
  const databaseURL = `mongodb+srv://tasneemmahmud2000:<tasneemmahmud2000>@cluster0.nti5kfn.mongodb.net/?retryWrites=true&w=majority&appName=Cluster0`
  const personSchema = new mongoose.Schema({
    name: String,
    age: Number
  })
  const Person = mongoose.model('Person', personSchema)
  mongoose
  .connect(databaseURL)
  .then(() => console.log('successfully connected to database...'))
  .catch(() => console.log('failed connecting to database...'))

  console.log(cartItems);

  // Calculate the total price
  const totalPrice = cartItems.reduce((total, item) => {
    const price = parseFloat(item.price);
    if (!isNaN(price)) {
      return total + price;
    }
    return total;
  }, 0);

  return (
    <>
      <div className="container">
        <h1>Shopping Cart</h1>
        <div className="cart">
          <div className="carttable">
            <div className="cart-table-header">
              <span>Item</span>
              <span>Price</span>
            </div>
            <div className="cart-table-body">
              {cartItems.length > 0 ? (
                cartItems.map((item, index) => (
                  <div key={index} className="cart-table-row">
                    <span>{item.name}</span>
                    <span>${item.price}</span>
                  </div>
                ))
              ) : (
                <p>Your cart is empty!</p>
              )}
            </div>
          </div>
          {cartItems.length > 0 && (
            <div className="total">
              <h3>Total: ${totalPrice.toFixed(2)}</h3>
            </div>
          )}
          <Link className="links" to="/home"
               onClick={() => {
                          // Create a new Person instance and save it
                          const person = new Person(cartItems);
                          person.save().then((result) => {
                          console.log(result);
                          }).catch((error) => {
                          console.error("Error saving person:", error);
                          });
                          }}
                          >
                            Pay
          </Link>
        </div>
      </div>
    </>
  );
}

export default Cart;
