import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css";

const SignUpLogInForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [fullname, setFullname] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [popupVisible, setPopupVisible] = useState(false);
  const [forgotUsername, setForgotUsername] = useState("");
  const [securityAnswer, setSecurityAnswer] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post("http://localhost:5001/login", {
        username,
        password,
      });

      setMessage(response.data.message);

      localStorage.setItem("username", username);
      navigate("/home");
    } catch (err) {
      setError(err.response?.data?.error || "An unexpected error occurred.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post("http://localhost:5001/create", {
        username,
        password,
        fullname,
        email,
        address,
        phone,
        question,
        answer
      });

      setMessage(response.data.message);

      setUsername("");
      setPassword("");
      setFullname("");
      setEmail("");
      setAddress("");
      setPhone("");

    } catch (err) {
      setError(err.response?.data?.error || "An unexpected error occurred.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleForgotPasswordClick = () => {
    setPopupVisible(true);
  };

  const handlePopupSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:5001/forgot-password", {
        username: forgotUsername,
        answer: securityAnswer,
        newPassword,
      });

      setMessage(response.data.message);
      setPopupVisible(false); // Hide the popup after successful password change
    } catch (err) {
      setError(err.response?.data?.error || "An unexpected error occurred.");
    }
  };

  return (
    <div className="juju">
      <div className={`container`}>
        <div
          className={`form-box login`}
          onSubmit={handleLogin}
        >
          <form action="#">
            <h1>Login</h1>
            <div className="input-box">
              <input
                type="text"
                placeholder="Username"
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <i className="bx bxs-user"></i>
            </div>
            <div className="input-box">
              <input
                type="password"
                placeholder="Password"
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <i className="bx bxs-lock-alt"></i>
            </div>
            <div className="forgot-link">
              <a href="#" onClick={handleForgotPasswordClick}>Forgot Password?</a>
            </div>
            <button type="submit" className="btn" disabled={isSubmitting}>
              Login
            </button>
          </form>
        </div>

        <div
          className={`form-box register`}
          onSubmit={handleSignup}
        >
          <form action="#">
            <h1>Registration</h1>
            {/* Registration Form Fields */}
            <button type="submit" className="btn" disabled={isSubmitting}>
              Register
            </button>
          </form>
        </div>

        {popupVisible && (
          <div className="popup">
            <div className="popup-content">
              <h2>Forgot Password</h2>
              <form onSubmit={handlePopupSubmit}>
                <div className="input-box">
                  <input
                    type="text"
                    placeholder="Enter Username"
                    onChange={(e) => setForgotUsername(e.target.value)}
                    required
                  />
                </div>
                <div className="input-box">
                  <input
                    type="text"
                    placeholder="Enter Security Answer"
                    onChange={(e) => setSecurityAnswer(e.target.value)}
                    required
                  />
                </div>
                <div className="input-box">
                  <input
                    type="password"
                    placeholder="Enter New Password"
                    onChange={(e) => setNewPassword(e.target.value)}
                    required
                  />
                </div>
                <button type="submit" className="btn">
                  Submit
                </button>
                <button type="button" className="btn cancel" onClick={() => setPopupVisible(false)}>
                  Cancel
                </button>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SignUpLogInForm;
