import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/Checkout.css';
import { Link } from 'react-router-dom';
import axios from 'axios';  // Import axios

function Cart() {
  const location = useLocation();
  const storedCart = localStorage.getItem('checkoutCart');
  const cartItems = location.state || (storedCart ? JSON.parse(storedCart) : []);
  const username = localStorage.getItem('user'); // Get the username from localStorage

  // Calculate the total price
  const totalPrice = cartItems.reduce((total, item) => {
    const price = parseFloat(item.price);
    if (!isNaN(price)) {
      return total + price;
    }
    return total;
  }, 0);

  const handlePayment = () => {
    // Prepare data for the backend
    const payload = {
      username, // Include username in the request
      cart: cartItems,
    };

    // Send cart items and username to the backend
    axios.post('http://localhost:5001/save-cart', payload)
      .then(response => {
        console.log(response.data);
        // Clear localStorage after successful payment
        localStorage.removeItem('cart');
        localStorage.removeItem('checkoutCart');
      })
      .catch(error => {
        console.error('Error saving cart:', error);
        // Handle error
      });
  };

  return (
    <>
      <div className="container">
        <h1>Shopping Cart</h1>
        <div className="cart">
          <div className="carttable">
            <div className="cart-table-header">
              <span>Item</span>
              <span>Price</span>
            </div>
            <div className="cart-table-body">
              {cartItems.length > 0 ? (
                cartItems.map((item, index) => (
                  <div key={index} className="cart-table-row">
                    <span>{item.name}</span>
                    <span>${item.price}</span>
                  </div>
                ))
              ) : (
                <p>Your cart is empty!</p>
              )}
            </div>
          </div>
          {cartItems.length > 0 && (
            <div className="total">
              <h3>Total: ${totalPrice.toFixed(2)}</h3>
            </div>
          )}
          <Link className="links" to="/home" onClick={handlePayment}>
            Pay
          </Link>
        </div>
      </div>
    </>
  );
}

export default Cart;
