import React, { useRef } from 'react';
import Slider from 'react-slick';
import '..//Carousel.css'; // Import the CSS file for styling
import 'slick-carousel/slick/slick.css'; // Import slick CSS
import 'slick-carousel/slick/slick-theme.css'; // Import slick theme CSS

const Carousel = () => {
  const sliderRef = useRef(null); // Create a ref for the slider

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1, // Show one image at a time
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  const handleClick = (e) => {
    const { clientX, target } = e;
    const { left, right } = target.getBoundingClientRect();
    if (clientX < (left + right) / 2) {
      sliderRef.current.slickPrev(); // Go to the previous slide
    } else {
      sliderRef.current.slickNext(); // Go to the next slide
    }
  };

  return (
    <div className="carousel-container" onClick={handleClick}>
      <Slider ref={sliderRef} {...settings}>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+1" alt="Image 1" />
        </div>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+2" alt="Image 2" />
        </div>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+3" alt="Image 3" />
        </div>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+4" alt="Image 4" />
        </div>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+5" alt="Image 5" />
        </div>
      </Slider>
    </div>
  );
};

export default Carousel;
