import React from 'react';
import { useNavigate } from 'react-router-dom';

function ProductCard({ product }) {
  const navigate = useNavigate();

  const handleAddToCartClick = () => {
    // Add to cart logic, you can use localStorage or context
    const cartItems = JSON.parse(localStorage.getItem('cart')) || [];
    cartItems.push({ name: product.name, price: product.price });
    localStorage.setItem('cart', JSON.stringify(cartItems));
    navigate('/cart');
  };

  return (
    <div className="product-card">
      {/* Directly use the full path to the image */}
      <img src={product.image} alt={product.name} className="product-image" />
      <h3>{product.name}</h3>
      <p>${product.price}</p>
      <button className="add-to-cart-button" onClick={handleAddToCartClick}>Add to Cart</button>
    </div>
  );
}

export default ProductCard;
