import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css'; // Make sure to import your CSS file

const SignUpLogInForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [fullname, setFullname] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [isActive, setIsActive] = useState(false);
  const [username2, setUsername2] = useState('');
  const [password2, setPassword2] = useState('');
  const [isSubmitting2, setIsSubmitting2] = useState(false);
  const [message2, setMessage2] = useState('');
  const [error2, setError2] = useState('');


  const handleRegisterClick = () => {
    setIsActive(true); // Set to true to show the Register form
  };

  const handleLoginClick = () => {
    setIsActive(false); // Set to false to show the Login form
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage('');
    setError('');

    try {
      const response = await axios.post('http://localhost:5001/login', {
        username,
        password,
      });

      setMessage(response.data.message);

      // Store session and redirect to home
      localStorage.setItem('username', username);
      navigate('/home');
    } catch (err) {
      setError(err.response?.data?.error || 'An unexpected error occurred.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage('');
    setError('');

    try {
      const response = await axios.post('http://localhost:5001/create', {
        username,
        password,
        fullname,
        email,
        address,
        phone,
      });

      setMessage(response.data.message);
      navigate('/'); // Redirect to login page after successful signup
    } catch (err) {
      setError(err.response?.data?.error || 'An unexpected error occurred.');
    } finally {
      setIsSubmitting(false);
    }
  };



  return (
    <div className="juju">
    <div className={`container ${isActive ? 'active' : ''}`}>
      <div className={`form-box login ${isActive ? 'hidden' : ''}`} onSubmit={handleLogin}>
        <form action="#">
          <h1>Login</h1>
          <div className="input-box">
            <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} required />
            <i className="bx bxs-user"></i>
          </div>
          <div className="input-box">
            <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} required />
            <i className="bx bxs-lock-alt"></i>
          </div>
          <div className="forgot-link">
            <a href="#">Forgot Password?</a>
          </div>
          <button type="submit" className="btn" disabled={isSubmitting2}>Login</button>

        </form>
      </div>

      <div className={`form-box register ${isActive ? 'visible' : ''}`} onSubmit={handleSignup}>
        <form action="#">
          <h1>Registration</h1>
          <div className="input-box">
            <input type="text" placeholder="Username" onChange={(e) => setUsername(e.target.value)} required />
            <i className="bx bxs-user"></i>
          </div>
          <div className="input-box">
            <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} required />
            <i className="bx bxs-envelope"></i>
          </div>
          <div className="input-box">
            <input type="fullname" placeholder="Full Name" onChange={(e) => setFullname(e.target.value)} required />
            <i className="bx bxs-lock-alt"></i>
          </div>
          <div className="input-box">
            <input type="address" placeholder="Address" onChange={(e) => setAddress(e.target.value)} required />
            <i className="bx bxs-lock-alt"></i>
          </div>
          <div className="input-box">
            <input type="phone" placeholder="Phone" onChange={(e) => setPhone(e.target.value)} required />
            <i className="bx bxs-lock-alt"></i>
          </div>
          <div className="input-box">
            <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} required />
            <i className="bx bxs-lock-alt"></i>
          </div>
          <button type="submit" className="btn" disabled={isSubmitting2} onClick={() => navigate('/')}>Register</button>

        </form>
      </div>

      <div className="toggle-box">
        <div className="toggle-panel toggle-left">
          <h1>Hello, Welcome!</h1>
          <p>Don't have an account?</p>
          <button className="btn register-btn" onClick={handleRegisterClick}>Register</button>
          {message && <p style={{ color: 'green' }}>{message}</p>}
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>

        <div className="toggle-panel toggle-right">
          <h1>Welcome Back!</h1>
          <p>Already have an account?</p>
          <button className="btn login-btn" onClick={handleLoginClick}>Login</button>
        </div>
      </div>
    </div>
    </div>
  );
};

export default SignUpLogInForm;
