import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/Cart.css';

function Cart() {
  const [cartItems, setCartItems] = useState(() => {
    // Retrieve cart items from localStorage if available
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });

  const location = useLocation();

  useEffect(() => {
    const newItem = location.state?.item;
    if (newItem) {
      setCartItems((prevItems) => {
        // Check if the item already exists in the cart
        const isItemAlreadyInCart = prevItems.some(
          (item) => item.name === newItem.name && item.price === newItem.price
        );
        if (!isItemAlreadyInCart) {
          const updatedCart = [...prevItems, newItem];
          // Save the updated cart to localStorage
          localStorage.setItem('cart', JSON.stringify(updatedCart));
          return updatedCart;
        }
        return prevItems;
      });
    }
  }, [location.state]);

  const removeItemFromCart = (index) => {
    setCartItems((prevItems) => {
      const updatedCart = prevItems.filter((_, i) => i !== index);
      // Update localStorage with the new cart state
      localStorage.setItem('cart', JSON.stringify(updatedCart));
      return updatedCart;
    });
  };

  // Ensure that price is a number and calculate the total price
  const totalPrice = cartItems.reduce((total, item) => {
    const p = parseInt(item.price);
    var v = total + p;
    console.log(typeofp)
    return v ;
  }, 0);

  console.log("Cart Items:", cartItems);  // Debugging cart items
  console.log("Total Price:", totalPrice);  // Debugging total price

  return (
    <div className="cart">
      <h2>Shopping Cart</h2>
      <ul>
        {cartItems.length > 0 ? (
          cartItems.map((item, index) => (
            <li key={index}>
              {item.name} - {item.price}
              <button onClick={() => removeItemFromCart(index)}>Remove</button>
            </li>
          ))
        ) : (
          <p>Your cart is empty!</p>
        )}
      </ul>
      {cartItems.length > 0 && (
        <div className="total">
          <h3>Total: ${totalPrice.toFixed(2)}</h3>
        </div>
      )}
    </div>
  );
}

export default Cart;
