import React, { useEffect, useState } from 'react';
import '../styles/Home.css';

function Profile() {
  const username = localStorage.getItem('username'); // Get username from localStorage
  const [items, setItems] = useState([]); // State to store user items
  let jj =[];

  useEffect(() => {
    if (username) {
      console.log('Fetching data for username:', username);
      fetch(`http://localhost:5001/items/tasneem`)  // Hardcoded to fetch items for 'tasneem'
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          jj = response.json;
          console.log(jj.length);
          return response.json();
        })
        .then((data) => {
          console.log('Fetched data:', data); // Inspect the fetched data
          if (data.error) {
            console.error(data.error);
          } else {
            setItems(data.tasneem || []); // Update state with user items
          }
        })
        .catch((error) => console.error('Error fetching items:', error));
    }
  }, [username]);


  return (
    <div>
      <h1>{username}'s Profile</h1>
      <div>
        {jj.length > 0 ? (
          items.map((item, index) => (
            <div key={index} className="item">
              <h2>{item.name}</h2>
              <p>Price: ${item.price}</p>
            </div>
          ))
        ) : (
          <p>No items found for this user.</p>
        )}
      </div>
    </div>
  );
}

export default Profile;
