import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css"; // Ensure this CSS file exists

// Loading Popup Component
const LoadingPopup = () => {
  return (
    <div className="loading-overlay">
      <div className="loading-popup">
        <div className="spinner"></div>
        <p>Logging in...</p>
      </div>
    </div>
  );
};

const SignUpLogInForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [fullname, setFullname] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [isActive, setIsActive] = useState(false);
  const [isLoggingIn, setIsLoggingIn] = useState(false);
  const [isSubmitting2, setIsSubmitting2] = useState(false);
  const navigate = useNavigate();

  const handleRegisterClick = () => setIsActive(true);
  const handleLoginClick = () => setIsActive(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoggingIn(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post("https://backendjt-1.onrender.com/login", {
        username,
        password,
      });

      setMessage(response.data.message);
      localStorage.setItem("username", username);
      navigate("/home");
    } catch (err) {
      setError(err.response?.data?.error || "An unexpected error occurred.");
    } finally {
      setIsLoggingIn(false);
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setIsSubmitting2(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post("https://backendjt-1.onrender.com/create", {
        username,
        password,
        fullname,
        email,
        address,
        phone,
        question,
        answer
      });

      setMessage(response.data.message);
      setUsername("");
      setPassword("");
      setFullname("");
      setEmail("");
      setAddress("");
      setPhone("");
      setIsActive(false);
    } catch (err) {
      setError(err.response?.data?.error || "An unexpected error occurred.");
    } finally {
      setIsSubmitting2(false);
    }
  };

  const handleForgotPassword = () => navigate("/forpass");

  return (
    <div className="juju">
      {isLoggingIn && <LoadingPopup />} {/* Show popup when logging in */}
      <div className={`container ${isActive ? "active" : ""}`}>
        {/* Login Form */}
        <div className={`form-box login ${isActive ? "hidden" : ""}`}>
          <form onSubmit={handleLogin}>
            <h1>Login</h1>
            <div className="input-box">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="input-box">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="forgot-link">
              <button type="button" onClick={handleForgotPassword} className="forgot-password-btn">
                Forgot Password?
              </button>
            </div>
            <button type="submit" className="btn" disabled={isLoggingIn}>
              {isLoggingIn ? "Logging in..." : "Login"}
            </button>
          </form>
        </div>

        {/* Registration Form */}
        <div className={`form-box register ${isActive ? "visible" : ""}`}>
          <form onSubmit={handleSignup}>
            <h1>Registration</h1>
            <div className="input-box">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="input-box">
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="input-box">
              <input
                type="text"
                placeholder="Full Name"
                value={fullname}
                onChange={(e) => setFullname(e.target.value)}
                required
              />
            </div>
            <div className="input-box">
              <input
                type="text"
                placeholder="Address"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                required
              />
            </div>
            <div className="input-box">
              <input
                type="text"
                placeholder="Phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
              />
            </div>
            <div className="input-box">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <div className="input-box">
              <select onChange={(e) => setQuestion(e.target.value)} required>
                <option value="">Select a security question</option>
                <option value="What is your father's first name?">What is your father's first name?</option>
                <option value="What is your mother's first name?">What is your mother's first name?</option>
                <option value="What is your best friend's first name?">What is your best friend's first name?</option>
              </select>
              <input
                type="text"
                placeholder="Answer"
                value={answer}
                onChange={(e) => setAnswer(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn" disabled={isSubmitting2}>
              {isSubmitting2 ? "Registering..." : "Register"}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SignUpLogInForm;
