import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import '../styles/Shop.css';
import { useNavigate } from 'react-router-dom';

function Shop() {
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [sortOrder, setSortOrder] = useState(''); // 'lowToHigh' or 'highToLow'
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch data from the backend API
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5001/products'); // Replace with your backend URL
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setItems(data);
        setFilteredItems(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  // Function to handle sorting
  const handleSortChange = (event) => {
    const order = event.target.value;
    setSortOrder(order);

    let sortedItems = [...items];
    if (order === 'lowToHigh') {
      sortedItems.sort((a, b) => a.price - b.price);
    } else if (order === 'highToLow') {
      sortedItems.sort((a, b) => b.price - a.price);
    }
    setFilteredItems(sortedItems);
  };

  // Function to handle cart icon click
  const handleCartClick = () => {
    const cartItems = JSON.parse(localStorage.getItem('cart')) || [];
    if (cartItems.length > 0) {
      navigate('/cart');
    } else {
      alert('Your cart is empty!');
    }
  };

  return (
    <div className="shop">
      <div className="filter-container">
        <label htmlFor="sort">Sort by Price:</label>
        <select id="sort" value={sortOrder} onChange={handleSortChange}>
          <option value="">Select</option>
          <option value="lowToHigh">Low to High</option>
          <option value="highToLow">High to Low</option>
        </select>
      </div>
      <div className="product-grid">
        {filteredItems.map(item => (
          <ProductCard key={item.id} product={item} />
        ))}
      </div>
      <div className="hiddenDiv" onClick={handleCartClick}>
        <i className="fas fa-shopping-cart"></i>
      </div>
    </div>
  );
}

export default Shop;
