import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';
import Carousel from '../components/Carousel';
import { Link } from 'react-router-dom';

function Home() {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const username = localStorage.getItem('username');
    if (username) {
      fetch('http://localhost:5001/get-role', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username }),
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.role) {
            setRole(data.role);
            if (data.role === 'admin') {
              navigate('/admin');
            }
          }
        })
        .catch((error) => console.error('Error fetching role:', error));
    } else {
      navigate('/login');
    }
  }, [navigate]);

  if (!role) {
    return <p>Loading...</p>;
  }

  // Function to handle cart icon click
  const handleCartClick = () => {
    // Check if the cart has items
    const cartItems = JSON.parse(localStorage.getItem('cart')) || []; // Get cart items from localStorage
    if (cartItems.length > 0) {
      navigate('/cart'); // Redirect to the /cart page if the cart has items
    } else {
      alert('Your cart is empty!'); // Optionally show a message if the cart is empty
    }
  };

  return (
    <div className="home">
      <Carousel />
      <h1>Welcome to Plink Bd</h1>
      <p>
      Welcome to Plink – Custom Made Cool!

At Plink, we make the little things feel special. From daily bags to travel tumblers, from pencil pouches to cosmetic cases—we customize everyday essentials to match your vibe. Whether you’re off to uni, heading to the office, or planning a weekend getaway, we’ve got you covered with cool, personalized pieces that feel just like you.

আমরা বিশ্বাস করি, shobkichu ektu nijer moto holei bhalo lage—that’s why every product at Plink is made with your name, your memories, and your personality in mind. Plus, our cute, minimal packaging (with a little surprise inside!) makes each order feel like a gift.

Need a unique gift for a friend? Or just want your own name on your travel mug?
Plink it your way.
Because ordinary’s boring, and you? You’re anything but that.
      </p>
      <Link to="/shop" className="shop-now-button">
        Shop Now
      </Link>
      <div className="hiddenDiv" onClick={handleCartClick}>
        <i className="fas fa-shopping-cart"></i>
      </div>
    </div>
  );
}

export default Home;
