import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css"; // Make sure to import your CSS file

const SignUpLogInForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [fullname, setFullname] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [isActive, setIsActive] = useState(false);
  const [username2, setUsername2] = useState("");
  const [password2, setPassword2] = useState("");
  const [isSubmitting2, setIsSubmitting2] = useState(false);
  const [message2, setMessage2] = useState("");
  const [error2, setError2] = useState("");
  const [popupVisible, setPopupVisible] = useState(false); // For controlling the popup
  const [securityQuestion, setSecurityQuestion] = useState(""); // To store the security question
  const [newPassword, setNewPassword] = useState(""); // For storing new password
  const [enteredUsername, setEnteredUsername] = useState(""); // For storing entered username

  const handleRegisterClick = () => {
    setIsActive(true); // Set to true to show the Register form
  };

  const handleLoginClick = () => {
    setIsActive(false); // Set to false to show the Login form
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post("http://localhost:5001/login", {
        username,
        password,
      });

      setMessage(response.data.message);

      // Store session and redirect to home
      localStorage.setItem("username", username);
      navigate("/home");
    } catch (err) {
      setError(err.response?.data?.error || "An unexpected error occurred.");
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setIsSubmitting2(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post("http://localhost:5001/create", {
        username,
        password,
        fullname,
        email,
        address,
        phone,
        question,
        answer
      });

      setMessage(response.data.message);

      // Reset form fields
      setUsername("");
      setPassword("");
      setFullname("");
      setEmail("");
      setAddress("");
      setPhone("");

      // Switch to login form
      setIsActive(false);
    } catch (err) {
      setError(err.response?.data?.error || "An unexpected error occurred.");
    } finally {
      setIsSubmitting2(false);
    }
  };

  const handleForgotPassword = async () => {
    try {
      // Send a request to check if the username exists
      const response = await axios.get(`http://localhost:5001/get-question-answer/${enteredUsername}`);

      // If the username exists, show the popup with the security question
      setSecurityQuestion(response.data.question);
      setPopupVisible(true);
    } catch (error) {
      setError("Username not found.");
    }
  };

  const handleChangePassword = async () => {
    try {
      // Verify the security answer and update password
      const response = await axios.post("http://localhost:5001/update-password", {
        username: enteredUsername,
        answer,
        newPassword,
      });

      setMessage(response.data.message);
      setPopupVisible(false); // Close the popup after successful password update
    } catch (error) {
      setError("Incorrect answer or error occurred.");
    }
  };

  return (
    <div className="juju">
      <div className={`container ${isActive ? "active" : ""}`}>
        <div
          className={`form-box login ${isActive ? "hidden" : ""}`}
          onSubmit={handleLogin}
        >
          <form action="#">
            <h1>Login</h1>
            <div className="input-box">
              <input
                type="text"
                placeholder="Username"
                onChange={(e) => setUsername(e.target.value)}
                required
              />
              <i className="bx bxs-user"></i>
            </div>
            <div className="input-box">
              <input
                type="password"
                placeholder="Password"
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <i className="bx bxs-lock-alt"></i>
            </div>
            <div className="forgot-link">
              <a href="#" onClick={handleForgotPassword}>Forgot Password?</a>
            </div>
            <button type="submit" className="btn" disabled={isSubmitting2}>
              Login
            </button>
          </form>
        </div>

        <div
          className={`form-box register ${isActive ? "visible" : ""}`}
          onSubmit={handleSignup}
        >
          <form action="#">
            <h1>Registration</h1>
            {/* Your registration fields here */}
            <button type="submit" className="btn" disabled={isSubmitting2}>
              Register
            </button>
          </form>
        </div>

        <div className="toggle-box">
          <div className="toggle-panel toggle-left">
            <h1>Hello, Welcome!</h1>
            <p>Don't have an account?</p>
            <button className="btn register-btn" onClick={handleRegisterClick}>
              Register
            </button>
            {message && <p style={{ color: "green" }}>{message}</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}
          </div>

          <div className="toggle-panel toggle-right">
            <h1>Welcome Back!</h1>
            <p>Already have an account?</p>
            <button className="btn login-btn" onClick={handleLoginClick}>
              Login
            </button>
          </div>
        </div>
      </div>

      {/* Popup for password reset */}
      {popupVisible && (
        <div className="popup">
          <div className="popup-content">
            <h2>Security Question</h2>
            <p>{securityQuestion}</p>
            <input
              type="text"
              placeholder="Answer"
              onChange={(e) => setAnswer(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="New Password"
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
            <button onClick={handleChangePassword}>Submit</button>
            <button onClick={() => setPopupVisible(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SignUpLogInForm;
