import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import '../styles/Checkout.css';
import { Link } from 'react-router-dom';
import axios from 'axios';  // Import axios

function Checkout() {
  const location = useLocation();
  const navigate = useNavigate();
  const storedCart = localStorage.getItem('checkoutCart');
  const cartItems = location.state || (storedCart ? JSON.parse(storedCart) : []);

  // Calculate the total price
  const handleApplyDiscount = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/discounts?discountCode=${discountCode}`);
      const discountData = response.data.discounts[0]; // Assuming discount code is unique
      if (discountData) {
        setDiscount(discountData.value);
        setError('');
      } else {
        setError('Invalid discount code');
      }
    } catch (error) {
      console.error('Error verifying discount code:', error);
      setError('Error applying discount');
    }
  };

  const totalPrice = cartItems.reduce((total, item) => {
    const price = parseFloat(item.price);
    return !isNaN(price) ? total + price : total;
  }, 0);

  const discountedPrice = totalPrice - (totalPrice * (discount / 100));

  // Check if the user is logged in
  const isLoggedIn = localStorage.getItem('username');

  const handlePayment = () => {
    // If the user is not logged in, redirect to login page
    if (!isLoggedIn) {
      navigate('/'); // Redirect to login page
      return;
    }

    // If the user is logged in, send cart items to the backend
    axios.post('http://localhost:5001/save-cart', cartItems)
      .then(response => {
        console.log(response.data);
        // Clear localStorage after successful payment
        localStorage.removeItem('cart');
        localStorage.removeItem('checkoutCart');

        // Redirect to home after successful payment
        navigate('/home');
      })
      .catch(error => {
        console.error('Error saving cart:', error);
        // Handle error
      });
  };


  return (
    <>
      <div className="container">
        <h1>Shopping Cart</h1>
        <div className="cart">
          <div className="carttable">
            <div className="cart-table-header">
              <span>Item</span>
              <span>Price</span>
            </div>
            <div className="cart-table-body">
              {cartItems.length > 0 ? (
                cartItems.map((item, index) => (
                  <div key={index} className="cart-table-row">
                    <span>{item.name}</span>
                    <span>${item.price}</span>
                  </div>
                ))
              ) : (
                <p>Your cart is empty!</p>
              )}
            </div>
          </div>
          {cartItems.length > 0 && (
            <div className="total">
              <h3>Total: ${totalPrice.toFixed(2)}</h3>
            </div>
          )}
          <button className="pay-button" onClick={handlePayment}>
            Pay
          </button>
        </div>
      </div>
    </>
  );
}

export default Checkout;
