import { useEffect, useState } from 'react';
import axios from 'axios';

const LoginForm = ({ onLogin, onCreate, onLogout, user }) => {
  const [message, setMessage] = useState('');

  // Effect to show success message when user is logged in or created
  useEffect(() => {
    if (message) {
      alert(message);
      setMessage(''); // Clear message after showing it
    }
  }, [message]);

  // Handler function for form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Grab form values
    const userData = {
      username: e.target.username.value,
      password: e.target.password.value,
    };

    // Determine which button was clicked
    const action = e.nativeEvent.submitter.value; // 'login' or 'create'

    try {
      let response;
      if (action === 'login') {
        // API call for login
        response = await axios.post('http://localhost:5001/login', userData);
        onLogin(response.data.user); // Pass logged-in user details to parent
      } else if (action === 'create') {
        // API call for user creation
        response = await axios.post('http://localhost:5001/create', userData);
        onCreate(response.data.user); // Optionally handle created user details
      }

      // Set success message
      setMessage(response.data.message);
    } catch (error) {
      // Set error message
      alert(error.response?.data?.error || 'An error occurred');
    }
  };

  return (
    <div>
      {user ? (
        <div className="username-header">
          <p>
            <strong>{user.username}</strong> is logged in
          </p>
          <button onClick={onLogout}>logout</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <div>
            <input placeholder="username" name="username" required />
          </div>
          <div>
            <input placeholder="password" name="password" type="password" required />
          </div>
          <div>
            <button type="submit" value="login">login</button>
            <button type="submit" value="create">create</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default LoginForm;
