import React, { useState } from "react";
import { FaFacebookF, FaTwitter, FaInstagram } from "react-icons/fa";
import "../styles/HomeAl.css";
import image1 from "../img/logo.png";
import image2 from "../img/logotransparent.png";

const StarbucksClone = () => {
  const images = [image1, image2];
  const [mainImage, setMainImage] = useState(images[0]); // Default image

  return (
    <div className="containerh">
      <div className="cardh">
        <div className="texsec">
          <div className="text-section">
            <h1>
              It's not just coffee
              <br />It's <span className="highlight">Starbucks</span>
            </h1>
            <p>
              Flavorful creations sure to brighten any summer day. Enjoy a powerful boost of
              energy handcrafted with sparkling fruit flavors. Try it frozen with a swirl of
              strawberry puree. Join Starbucks and savor our drinks made specially for you.
            </p>
            <button className="learn-more">Learn More</button>
          </div>

          <div className="small-images">
            {images.map((img, index) => (
              <img
                key={index}
                src={img}
                alt={`Drink ${index + 1}`}
                onClick={() => setMainImage(img)}
                style={{ cursor: "pointer" }}
              />
            ))}
          </div>
        </div>

        <div className="imsec">
          <div className="image-section">
            <img src={mainImage} alt="Green Drink" className="main-image" />
          </div>

          <div className="social-icons">
            <FaFacebookF />
            <FaTwitter />
            <FaInstagram />
          </div>
        </div>
      </div>
    </div>
  );
};

export default StarbucksClone;
