import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';
import Carousel from '../components/Carousel';
import { Link } from 'react-router-dom';

function Home() {
  const [loading, setLoading] = useState(true);
  const [role, setRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserRole = async () => {
      try {
        const response = await fetch('/api/user');
        const data = await response.json();
        const userRole = data.role;

        console.log('Fetched user role:', userRole); // Log the fetched role

        if (userRole === 'admin') {
          navigate('/admin'); // Navigate to admin page
        } else {
          setRole(userRole); // Set role for non-admin users
        }
      } catch (error) {
        console.error('Error fetching user role:', error);
      } finally {
        setLoading(false); // Stop loading in all cases
      }
    };

    fetchUserRole();
  }, [navigate]);

  if (loading) {
    return <div>Loading...</div>; // Show a loading state
  }

  // Content to show only if the user is not an admin
  return (
    <div className="home">
      {role !== 'admin' && (
        <>
          <Carousel />
          <h1>Welcome to Jaanu Tuni Website</h1>
          <p>
            At Jaanu Tuni, we believe in celebrating individuality and self-expression. Our "make-your-own"
            platform lets you design your world, your way. Choose from a variety of unique designs or bring your own ideas to life, and
            we’ll print them on clothing, accessories, mugs, and more. Whether it’s a gift, a statement piece,
            or something truly personal, Jaanu Tuni is here to help you stand out and make every item uniquely yours. Start creating today
            and let your imagination shine!
          </p>
          <Link to="/shop" className="shop-now-button">Shop Now</Link>
        </>
      )}
    </div>
  );
}

export default Home;
