import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/ProductCard.css';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loggedInUser, setLoggedInUser] = useState(null);

  const navigate = useNavigate(); // Initialize navigate hook

  // Check for an existing session on component mount
  useEffect(() => {
    const storedUser = localStorage.getItem('username');
    if (storedUser) {
      setLoggedInUser(storedUser);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage('');
    setError('');

    const action = e.nativeEvent.submitter.value; // 'login' or 'create'

    try {
      const endpoint = action === 'login' ? '/login' : '/create';
      const response = await axios.post(`http://localhost:5001${endpoint}`, {
        username,
        password,
      });

      setMessage(response.data.message);

      if (action === 'login') {
        // Store the username in localStorage for session management
        localStorage.setItem('username', username);
        setLoggedInUser(username);

        // Redirect to /home on successful login
        navigate('/home');
      }

      setUsername('');
      setPassword('');
    } catch (err) {
      if (err.response) {
        setError(err.response.data.error);
      } else {
        setError('An unexpected error occurred.');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('username');
    setLoggedInUser(null);
    setMessage('You have been logged out.');
  };

  return (
    <div>
      {loggedInUser ? (
        <div>
          <p>Welcome, {loggedInUser}!</p>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <form onSubmit={handleSubmit}>
          <div className="inp">
            <label>Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="inp">
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" value="login" disabled={isSubmitting}>
            Login
          </button>
          <button type="submit" value="create" disabled={isSubmitting}>
            Create Account
          </button>
        </form>
      )}
      {message && <p style={{ color: 'green' }}>{message}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default LoginForm;
