import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import '../styles/Shop.css';
import { useNavigate } from 'react-router-dom';

const AddPro = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [image, setImage] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const categories = ['Electronics', 'Clothing', 'Home Appliances', 'Books', 'Toys'];

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !price || !category || !image) {
      alert('Please fill out all fields before submitting.');
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('price', price);
    formData.append('category', category);
    formData.append('image', image);

    setIsSubmitting(true);

    try {
      const response = await axios.post('http://localhost:5001/products', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      console.log(response.data);
      alert('Product added successfully!');

      setName('');
      setPrice('');
      setCategory('');
      setImage(null);
    } catch (error) {
      console.error('Error adding product:', error);
      alert('Failed to add product');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="add-pro-container">
      <h2 className="add-pro-title">Add Product</h2>
      <form onSubmit={handleSubmit} className="add-pro-form">
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} className="add-pro-input" />
        </div>
        <div>
          <label>Price:</label>
          <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} className="add-pro-input" />
        </div>
        <div>
          <label>Category:</label>
          <select value={category} onChange={(e) => setCategory(e.target.value)} className="add-pro-select">
            <option value="" disabled>Select a category</option>
            {categories.map((cat, index) => (
              <option key={index} value={cat}>{cat}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Image:</label>
          <input type="file" onChange={(e) => setImage(e.target.files[0])} className="add-pro-input" />
        </div>
        <button type="submit" className="add-pro-button" disabled={isSubmitting}>
          {isSubmitting ? 'Adding...' : 'Add Product'}
        </button>
      </form>
    </div>
  );
};

export default AddPro;
