import React, { useRef } from 'react';
import Slider from 'react-slick';
import '../styles/Carousel.css'; // Import the CSS file for styling
import 'slick-carousel/slick/slick.css'; // Import slick CSS
import 'slick-carousel/slick/slick-theme.css'; // Import slick theme CSS
import image1 from '../images/image1.jpg';
import image2 from '../images/image2.jpg';
import image3 from '../images/image3.jpg';
import image4 from '../images/image4.jpg';
import image5 from '../images/image5.jpg';
const Carousel = () => {
  const sliderRef = useRef(null); // Create a ref for the slider

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1, // Show one image at a time
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  return (
    <div className="carousel-container">
      <Slider ref={sliderRef} {...settings}>
        <div className="carousel-item">
          <img src={image1} alt="Image 1" />
        </div>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+2" alt="Image 2" />
        </div>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+3" alt="Image 3" />
        </div>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+4" alt="Image 4" />
        </div>
        <div className="carousel-item">
          <img src="https://via.placeholder.com/1920x300?text=Image+5" alt="Image 5" />
        </div>
      </Slider>
      <div className="carousel-buttons">
        <button className="slick-prev" onClick={() => sliderRef.current.slickPrev()}>&lt;</button>
        <button className="slick-next" onClick={() => sliderRef.current.slickNext()}>&gt;</button>
      </div>
    </div>
  );
};

export default Carousel;
